mapper.delete:
    
      DELETE
		FROM seat_reservation
		WHERE franchisee_no = #{franchiseeNo} AND seat_no = #{seatNo}

mapper.update:

	UPDATE food
	SET 
		food_name = #{foodName},
		food_category = #{foodCategory},
		food_price = #{foodPrice}
	WHERE food_no = #{foodNo}

mapper.select:
SELECT food_no as foodNo, food_name as foodName, food_category as foodCategory, food_price as foodPrice FROM food
franchisee_no = #{franchiseeNo}
AND food_category = #{foodCategory}

mapper.update:

  UPDATE p_customer SET 
     customer_name = #{customerName},
     customer_email = #{customerEmail},
     customer_phone = #{customerPhone}
  WHERE 
     customer_no = #{customerNo}


	UPDATE franchisee_spec
	SET
		cpu = #{cpu},
		vga = #{vga},
		ram = #{ram}
	WHERE franchisee_no = #{franchiseeNo}

mapper.select:
SELECT faq_no as faqNo, faq_title as faqTitle, faq_content as faqContent FROM franchisee_faq
faq_title like CONCAT('%', #{searchWord}, '%')
LIMIT #{beginRow}, #{rowPerPage}


   SELECT seq_no
   FROM franchisee_seq
   LIMIT 1;
